mapper.select:



        select handNum, mId, handTitle, handContent, handType, handPrice,handDate, handUpdate from(

                select /*+INDEX_DESC(handmade pk_handmade) */ rownum  as rn, handNum, mId, handTitle, handContent, handType, handPrice, handDate, handUpdate

                from handmade
                where 
                rownum <= #{cri.pageNum} * #{cri.amount}



						and handType = #{handType}

					)

        where rn > (#{cri.pageNum} -1) * #{cri.amount}

mapper.update:
update handmade
set
handTitle =
#{handTitle}, handContent=#{handContent}, handType=#{handType}
where
handNum = #{handNum} and mId = #{mId}

mapper.delete:
delete from handmade
where
mId=#{mId} and handNum = #{handNum}
